import { Link } from "react-router-dom";
function Places() {
    return (
        <>
            <div className="bg-image" 
            style={{ 
                backgroundImage: "url('https://img.freepik.com/premium-photo/soft-blur-nature-background-abstract-modern-website-graphics-with-smooth-gradient-background_532332-40.jpg')", 
                backgroundRepeat: "no-repeat",
                backgroundSize: "100% 100%",
                height: "auto" 
            }}>
                <div className="p-4">
                    <h1 className="text-center text-bg-dark"
                        style={{ fontFamily: "monospace" }}
                    >Find Your Destination...</h1>
                    <div className="container mt-5">
                        <form className="d-flex" role="search">
                            <input className="form-control me-2 bg-success-subtle" type="search" placeholder="Search Your Destination!" aria-label="Search" />
                            <button className="btn btn-outline-secondary text-light" type="submit">
                                Go To Your Destination
                            </button>
                        </form>
                    </div>

                    <h1 className="mt-5 text-center text-warning-emphasis"
                        style={{ fontFamily: "monospace" }}>Our Popular Places</h1>

                    <div>
                        <div className="d-flex flex-wrap justify-content-between p-5">
                            {["GOA", "MANALI", "KASHMIR", "KERALA", "LADAKH"].map((place, idx) => (
                                <div key={idx} className="card-container">
                                    <div className="card m-2" style={{ width: '18rem' }}>
                                        <img src={`https://your-image-url/${place.toLowerCase()}.jpg`} className="card-img-top" alt={place} />
                                        <div className="card-body bg-info-subtle">
                                            <h5 className="card-title text-center"><b>{place}</b></h5>
                                            <p className="card-text text-center">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                            <div className="text-center">
                                                <Link to="#" className="btn btn-outline-secondary">Book Now</Link>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
            <style jsx>{`
                .card-container {
                    transition: transform 0.3s ease;
                }

                .card-container:hover {
                    transform: scale(1.2);
                }
            `}</style>
        </>
    );
}

export default Places;
