import React, { useState, useEffect } from 'react';

const BookNow = () => {
    const [Bookings, setBookings] = useState({
        name: "",
        email: "",
        destination: "",
        date: ""
    });

    const [allBookings, setAllBookings] = useState([]);
    const [editing, setEditing] = useState(false); // State to track if we're editing
    const [editId, setEditId] = useState(null); // Track the id of the booking being edited

    // Fetch all bookings when component loads
    useEffect(() => {
        fetchBookings();
    }, []);

    const fetchBookings = async () => {
        try {
            const response = await fetch("http://localhost:3001/bookings");
            const data = await response.json();
            setAllBookings(data);
        } catch (error) {
            console.error("Error fetching bookings:", error);
        }
    };

    // Add or update booking
    const handleSaveBooking = async (e) => {
        e.preventDefault();
        const url = editing 
            ? `http://localhost:3001/bookings/${editId}` 
            : "http://localhost:3001/bookings";
        const method = editing ? "PUT" : "POST";

        try {
            const response = await fetch(url, {
                method: method,
                body: JSON.stringify(Bookings),
                headers: {
                    "Content-Type": "application/json"
                }
            });

            if (response.ok) {
                const result = await response.json();
                alert(editing ? "Booking updated successfully!" : "Booking added successfully!");
                console.log(result);
                fetchBookings(); // Reload bookings
                resetForm();
            } else {
                console.error("Error:", response.statusText);
            }
        } catch (err) {
            console.error("Error saving booking:", err);
        }
    };

    // Delete a booking
    const handleDeleteBooking = async (id) => {
        try {
            const response = await fetch(`http://localhost:3001/bookings/${id}`, {
                method: "DELETE"
            });
            if (response.ok) {
                alert("Booking deleted successfully!");
                fetchBookings(); // Reload bookings
            } else {
                console.error("Error:", response.statusText);
            }
        } catch (err) {
            console.error("Error deleting booking:", err);
        }
    };

    // Edit booking
    const handleEditBooking = (booking) => {
        setBookings(booking);
        setEditId(booking.id);
        setEditing(true);
    };

    // Reset form after adding or editing
    const resetForm = () => {
        setBookings({ name: "", email: "", destination: "", date: "" });
        setEditId(null);
        setEditing(false);
    };

    const handleChange = (e) => {
        setBookings({
            ...Bookings,
            [e.target.name]: e.target.value
        });
    };

    return (
        <>
            <div className="bg-image" 
                style={{ 
                    backgroundImage: "url('https://img.freepik.com/premium-photo/soft-blur-nature-background-abstract-modern-website-graphics-with-smooth-gradient-background_532332-40.jpg')", 
                    backgroundRepeat: "no-repeat",
                    backgroundSize: "100% 100%",
                    height: "auto"
                }}>
                <div className='bg-image opacity-50'
                    style={{
                        backgroundImage: "url('https://media.istockphoto.com/id/1097481110/photo/passenger-plane-business-trip-travel-concept-flying-evening-sunset.jpg?s=612x612&w=0&k=20&c=f2itQWoW1ObaBI0wCQsaT2s7cpo5a_CIqEQItDW8g90=')", 
                        backgroundRepeat: "no-repeat",
                        backgroundSize: "100% 100%",
                        height: "40vh"
                    }}
                >
                    <h1 className='text-center me-1'><b>Arrived</b></h1>
                    <p className='text-center fs-4 me-1'><i>Unlock the best in Travel!</i></p>
                </div>
                <div className="container">
                    <h1 className="text-center text-light-emphasis"><b>{editing ? "Edit" : "Book"} Your Destination</b></h1>
                    <form className="mt-4" onSubmit={handleSaveBooking}>
                        <div className="mb-3">
                            <label className="form-label">Full Name</label>
                            <input
                                type="text"
                                className="form-control"
                                name="name"
                                placeholder='Please Enter Your Full Name'
                                value={Bookings.name}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                name="email"
                                placeholder='Please Enter Your E-mail'
                                value={Bookings.email}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Destination</label>
                            <input
                                type="text"
                                className="form-control"
                                name="destination"
                                placeholder='Please Enter Your Dream Place'
                                value={Bookings.destination}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Travel Date</label>
                            <input
                                type="date"
                                className="form-control"
                                name="date"
                                value={Bookings.date}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <br/>
                        <button type="submit" className="btn btn-outline-secondary text-light">
                            {editing ? "Update" : "Submit"}
                        </button>
                        {editing && <button type="button" className="btn btn-outline-warning text-light ms-2" onClick={resetForm}>Cancel</button>}
                    </form>

                    <div className="mt-5">
                        <h2 className="text-center">Your Bookings</h2>
                        <ul className="list-group">
                            {allBookings.map((booking) => (
                                <li className="list-group-item" key={booking.id}>
                                    <strong>{booking.name}</strong> - {booking.destination} on {booking.date}
                                    <button className="btn btn-sm btn-outline-danger ms-3" onClick={() => handleDeleteBooking(booking.id)}>Delete</button>
                                    <button className="btn btn-sm btn-outline-primary ms-2" onClick={() => handleEditBooking(booking)}>Edit</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
}

export default BookNow;
