import React, { useState } from 'react';

const BookNow = () => {
    const [data, setData] = useState([]);
	const [isFormUpdating, setIsFormUpdating] = useState(false);
	const [Bookings, setBookings] = useState({
        name: "",
        email: "",
        destination: "",
        date: ""
    });

    // Get All Bookings
    useEffect(() => {
		getAllData();
	}, []);

	const getAllData = async () => {
		await fetch("http://localhost:3001/bookings")
			.then((res) => { return res.json() })
			.then((res) => { setBookings(res) })
	}


    // Add Bookings
    const addBookings = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch("http://localhost:3001/bookings", {
                method: "POST",
                body: JSON.stringify(Bookings),
                headers: {
                    "Content-Type": "application/json"
                }
            });
            console.log(res);
            if (res.ok) {
                const result = await res.json();
                alert("Booking Added Successfully!");
                console.log(result);
            } else {
                console.error("Error:", res.statusText);
            }
            getAllData();
        } catch (err) {
            console.error("Error On booking:", err);
        }
    };

    const handleChange = (e) => {
        setBookings({
            ...Bookings,
            [e.target.name]: e.target.value
        });
    };

    // Delete Bookings
    const deleteBookings = async (name) => {
		await fetch("http://localhost:3001/bookings" + name, {
			method: "DELETE"
		});
		getAllData();
	}

    // Update Bookings
    const updateBookings = async () => {
        await fetch("http:localhost:3001/bookings"+Bookings.name , {
            method:"PUT",
            body: JSON.stringify(Bookings),
        })
    }

    return (
        <>
            <div className="bg-image" 
                style={{ 
                    backgroundImage: "url('https://img.freepik.com/premium-photo/soft-blur-nature-background-abstract-modern-website-graphics-with-smooth-gradient-background_532332-40.jpg')", 
                    backgroundRepeat: "no-repeat",
                    backgroundSize: "100% 100%",
                    height: "auto"
                }}>
                <div className='bg-image opacity-50'
                    style={{
                        backgroundImage: "url('https://media.istockphoto.com/id/1097481110/photo/passenger-plane-business-trip-travel-concept-flying-evening-sunset.jpg?s=612x612&w=0&k=20&c=f2itQWoW1ObaBI0wCQsaT2s7cpo5a_CIqEQItDW8g90=')", 
                        backgroundRepeat: "no-repeat",
                        backgroundSize: "100% 100%",
                        height: "40vh"
                    }}
                >
                    <h1 className='text-center me-1'><b>Arrived</b></h1>
                    <p className='text-center fs-4 me-1'><i>Unlock the best in Travel!</i></p>
                </div>
                <div className="container">
                <h1 className="text-center text-light-emphasis"><b>Book Your Destination</b></h1>
                    <form className="mt-4" onSubmit={addBookings}>
                        <div className="mb-3">
                            <label className="form-label">Full Name</label>
                            <input
                                type="text"
                                className="form-control"
                                name="name"
                                placeholder='Please Enter Your Full Name'
                                value={Bookings.name}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                name="email"
                                placeholder='Please Enter Your E-mail'
                                value={Bookings.email}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Destination</label>
                            <input
                                type="text"
                                className="form-control"
                                name="destination"
                                placeholder='Please Enter Your Dream Place'
                                value={Bookings.destination}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Travel Date</label>
                            <input
                                type="date"
                                className="form-control"
                                name="date"
                                value={Bookings.date}
                                onChange={handleChange}
                                required
                            />
                        </div><br/>
                        <button type="submit" className="btn btn-outline-secondary text-light">Submit</button>

                    </form>

                    {
							Bookings.map((Bookings) => {
								return <tr className='border border-black' key={Bookings.name}>
									<td>{Bookings.name}</td>
									<td>{Bookings.email}</td>
                                    <td>{Bookings.destination}</td>
                                    <td>{Bookings.date}</td>
									<td>
										<button className='p-2 rounded-md text-white bg-yellow-500'
											onClick={() => {
											}}
										>Update</button>
										<button className='p-2 ml-2 rounded-md text-white bg-red-600' onClick={() => {
											deleteBookings(Bookings.name);
										}}>Delete</button>
									</td>
								</tr>
							})
						}
                </div>
            </div>
        </>
    );
}

export default BookNow;
